class BoundingCoordinates {
    private long top;
    private long bottom;
    private long left;
    private long right;
    private String url;

    public BoundingCoordinates(JSONObject obj) {
        try {
            this.top = obj.getLong("top");
            this.bottom = obj.getLong("bottom");
            this.left = obj.getLong("left");
            this.right = obj.getLong("right");
            this.url = obj.getString("url");
        } catch (JSONException e) {
            return;
        }
    }

    public String getURL() {
        return this.url;
    }

    public boolean in(float x, float y) {
        return (this.top <= y && y <= this.bottom && this.left <= x && x <= this.right);
    }

}

