    private HashSet<BoundingCoordinates> links = new HashSet<BoundingCoordinates>();
    private int image_width = 1;
    private int image_height = 1;
    private EditText edittext;
    private ImageView image_view;

    private int start = -1;
    private int end = -1;

    private void process_data(String data) {

        JSONObject obj;
        try {
            obj = new JSONObject(data);
            this.display_image(obj.getString("i"));
            this.links.clear();
            JSONArray links_obj = obj.getJSONArray("links");
            for (int i = 0; i < links_obj.length(); i++) {
                BoundingCoordinates bound = new BoundingCoordinates(links_obj.getJSONObject(i));
                this.links.add(bound);
            }
            this.image_width = obj.getInt("w");
            this.image_height = obj.getInt("h");

        } catch (JSONException e) {
            return;
        }
    }

    private void display_image(String image_str) {
        byte[] imageBytes = Base64.decode(image_str, Base64.DEFAULT);
        Bitmap decodedImage = BitmapFactory.decodeByteArray(imageBytes, 0, imageBytes.length);
        if (decodedImage == null) {
            return;
        }
        decodedImage = decodedImage.copy(Bitmap.Config.ARGB_8888 , true);
        int[] pixel_map = new int[]{22, 58, 95, 136, 174, 206, 234, 254};
        for (int r = 0; r < decodedImage.getHeight(); r++) {
            for (int c = 0; c < decodedImage.getWidth(); c++) {
                int v = pixel_map[decodedImage.getPixel(c, r) & 0xFF];
                int p = (0xFF << 24) | (v << 16) | (v << 8) | v;
                decodedImage.setPixel(c, r, p);
            }
        }

        this.image_view.setImageBitmap(decodedImage);
    }

    public IncomingSms(ImageView image_view, EditText edittext) {
        this.image_view = image_view;
        this.edittext = edittext;
        image_view.setOnTouchListener(new View.OnTouchListener() {
            @Override
            public boolean onTouch(View v, MotionEvent event) {
                if (event.getAction() == MotionEvent.ACTION_DOWN){

                    int window_width = IncomingSms.this.image_view.getMeasuredWidth();
                    int window_height = IncomingSms.this.image_view.getMeasuredHeight();
                    float scale_width = 980 / ((float)window_width);
                    float scale_height = 1743 / ((float)window_height);

                    float x = event.getX() * scale_width;
                    float y = event.getY() * scale_height;

                    // check if clicked link

                    for (BoundingCoordinates bounds : IncomingSms.this.links) {
                        System.out.println("HERE");
                        IncomingSms.this.edittext.setText(bounds.getURL());
                        //sms.sendTextMessage("5558", null, IncomingSms.this.edittext.getText().toString(), null, null);
                    }
                    System.out.println(String.valueOf(x) + ", " + String.valueOf(y));
                }
                return true;
            }
        });

        this.process_data(this.initial_state);
    }

    @Override
    public void onReceive(Context context, Intent intent) {
        Bundle intentExtras = intent.getExtras();

        if (intentExtras != null) {
            Object[] sms = (Object[]) intentExtras.get(SMS_BUNDLE);
            String message = "";
            for (int i = 0; i < sms.length; ++i) {
                String format = intentExtras.getString("format");
                SmsMessage smsMessage = SmsMessage.createFromPdu((byte[]) sms[i], format);

                String smsBody = smsMessage.getMessageBody().toString();
                String address = smsMessage.getOriginatingAddress();

                message += smsBody;
            }

            int index;
            String _message;
            if (message.charAt(0) == '#') {
                index = Integer.parseInt(message.substring(1, 5));
                int end = Integer.parseInt(message.substring(5, 9));
                this.start = index;
                this.end = end;
                _message = message.substring(9);
            } else {
                index = Integer.parseInt(message.substring(0, 4));
                _message = message.substring(4);
            }

            this.messages[index] = _message;
            System.out.println(index);

            if (this.start >= 0 && this.end >= 0) {
                int i = this.start;
                boolean reached = false;
                boolean flag = true;
                while (!reached) {
                    if (this.messages[i] == null) {
                        flag = false;
                        break;
                    }

                    if (i == this.end) {
                        reached = true;
                    }
                    i = (i + 1) % 1000;
                }

                if (flag) {
                    String data = "";

                    i = this.start;

                    reached = false;
                    while (!reached) {
                        data += messages[i];
                        messages[i] = null;
                        if (i == this.end) {
                            reached = true;
                        }
                        i = (i + 1) % 1000;
                    }
                    this.process_data(data);
                }
            }

        }


    }
